  {
  "use strict";
  $('#top-nav .menu-item-has-children > a, #main-nav ul li ul .menu-item-has-children > a').append(' +');
  $('#main-nav .menu-item-has-children > a').append('<span class="menu-arrow"></span>');
  $('#logo').after('<div class="hamburger closed" id="menu-icon"><div class="burger-main"><div class="burger-inner"><span class="top"></span><span class="mid"></span><span class="bot"></span></div></div><div class="svg-main"><svg class="svg-circle"><path class="path" fill="none" stroke="#00a2ff" stroke-miterlimit="10" stroke-width="4" d="M 34 2 C 16.3 2 2 16.3 2 34 s 14.3 32 32 32 s 32 -14.3 32 -32 S 51.7 2 34 2"/></svg></div><div class="path-burger"><div class="animate-path"><div class="path-rotation"></div></div></div></div>');
  $('#top-nav .wrapper').append('<div id="top-menu-icon"></div>');
  $('#menu-icon').on('click',function()
    {
    $('#main-nav').slideToggle(250);
    $(this).toggleClass('active')
  }
  );
  $(window).resize(function()
    {
    var w=$(window).width();
    var navDisplay=$('#main-nav');
    if(w>1000&&navDisplay.is(':hidden'))
      {
      navDisplay.removeAttr('style')
    }
  }
  );
  $('#top-menu-icon').on('click',function()
    {
    $('#top-nav ul').slideToggle(250);
    $(this).toggleClass('active')
  }
  );
  $(window).resize(function()
    {
    var w=$(window).width();
    var navDisplay=$('#top-nav ul');
    if(w>1000&&navDisplay.is(':hidden'))
      {
      navDisplay.removeAttr('style')
    }
  }
  )
}
