if (selectchap == false) { var yktocs = function (data) { var entriesHtml = []; if (data.feed && data.feed.entry) { for (var i = 0; i < data.feed.entry.length; i++) { for (var j = 0; j < data.feed.entry[i].link.length; j++) { if ("alternate" == data.feed.entry[i].link[j].rel) { var entityUrl = data.feed.entry[i].link[j].href; entriesHtml.push("<a href='" + entityUrl + "' title='Index Novel'>" + postIndex + "</a>"); break; } } } document.getElementById("indexjs").innerHTML = entriesHtml.join(""); } }; var ykpager = function (data) { var node = { postlist: [] }; var i = 0; for (; i < data.feed.entry.length; i++) { var j = 0; for (; j < data.feed.entry[i].link.length; j++) { if ("alternate" == data.feed.entry[i].link[j].rel) { var murl = data.feed.entry[i].link[j].href; break; } } var videoId = data.feed.entry[i].id.$t; var CAPTURE_ID = videoId.substr(51, 19); node.postlist.push({ url: murl, id: CAPTURE_ID }); } var v = node.postlist.findIndex((timeline_mode) => { return timeline_mode.id == postID; }); var id = v + 1; var cbs = node.postlist[id]; if (cbs) { var g = Object.values(cbs)[0]; var lnkDiv = document.getElementById("prevjs"); lnkDiv.innerHTML = "<a href='" + g + "' title='Chapter Sebelumnya'>" + postPrev + "</a>"; } var q = v - 1; var val = node.postlist[q]; if (val) { var k = Object.values(val)[0]; lnkDiv = document.getElementById("nextjs"); lnkDiv.innerHTML = "<a href='" + k + "' title='Chapter Selanjutnya'>" + postNext + "</a>"; } }; } else { var style = document.createElement('style'); style.textContent = '.pager-js{display:none;}'; document.head.appendChild(style); }
/**/

var postID = '';
var autlist = false;
var excludedLabels = ['Novel'];
var currentLabel = '';
var numChapters = 0;
var encounteredEntryIDs = {};
var hasExcludedLabel = false;
var hasChapterLabel = false;
var hasNonChapterLink = false;
var similarLabel = null; // Variable to store the similar label
//
if (excludedLabels.indexOf(currentLabel) === -1) { autlist = true; } else if (currentLabel === 'Chapter') { hasChapterLabel = true; } function shouldDisplayEntry(entryLabels) { return ( autlist || (entryLabels.includes('Chapter') && entryLabels.length === 2) ); } if (autlist || hasChapterLabel) { function autolist(e) { var listContainer = document.querySelector('.auto-listchap'); var chapNumElement = document.getElementById('chap-num'); var clOpenModal = document.getElementById('clOpen'); for (var t = 0; t < e.feed.entry.length; t++) { var entryLabels = []; var entryID = e.feed.entry[t].id.$t.substr(51, 19); var hasNonChapterLinkEntry = false; similarLabel = null; for (var l = 0; l < e.feed.entry[t].category.length; l++) { var label = e.feed.entry[t].category[l].term; entryLabels.push(label); if (excludedLabels.indexOf(label) !== -1) { hasExcludedLabel = true; } if (label !== 'Chapter' && entryLabels.length === 1) { similarLabel = label; } } if (shouldDisplayEntry(entryLabels) && entryID !== postID && !encounteredEntryIDs[entryID]) { var listItem = document.createElement('div'); var linkElement = document.createElement('a'); var dateElement = document.createElement('time'); var publishedDate = e.feed.entry[t].published.$t; for (var r = 0; r < e.feed.entry[t].link.length; r++) { if ('alternate' == e.feed.entry[t].link[r].rel) { var link = e.feed.entry[t].link[r].href; break; } } var title = e.feed.entry[t].title.$t; linkElement.href = link; linkElement.textContent = title; dateElement.className = 'publish-date timeago fs-08'; dateElement.setAttribute('data-publish', 'data:post.date.iso8601'); dateElement.setAttribute('title', publishedDate); dateElement.textContent = ''; listItem.appendChild(linkElement); listItem.appendChild(dateElement); listContainer.appendChild(listItem); numChapters++; encounteredEntryIDs[entryID] = true; } } if (numChapters === 0) { chapNumElement.textContent = 'No Chapter Yet'; clOpenModal.style.display = 'none'; } else { if (hasNonChapterLink) { numChapters--; } chapNumElement.textContent = numChapters; } } }
//

b:loop.script:
//
  // chaplist Modal
  var clModal = document.getElementById('clOpen'); var clBtn = document.getElementById('clBtn'); var clClose = document.getElementById('clClose'); clBtn.onclick = function () { openChaplistModal(); }; clClose.onclick = function () { closeChaplistModal(); }; function openChaplistModal() { clModal.style.display = 'block'; setTimeout(function () { clModal.style.opacity = '1'; }, 10); } function closeChaplistModal() { clModal.style.opacity = '0'; setTimeout(function () { clModal.style.display = 'none'; }, 300); }
  //

b:if.script:
var numChapters = 0; 
var postID = '';
//
if (autlist === true) { function autolist(e) { var listContainer = document.querySelector('.auto-listchap'); for (var t = 0; t < e.feed.entry.length; t++) { for (var r = 0; r < e.feed.entry[t].link.length; r++) { if ("alternate" === e.feed.entry[t].link[r].rel) { var link = e.feed.entry[t].link[r].href; break; } } var title = e.feed.entry[t].title.$t; var entryID = e.feed.entry[t].id.$t.substr(51, 19); var isCurrentPost = entryID === postID; var listItem = document.createElement('div'); var linkElement = document.createElement('a'); var dateElement = document.createElement('time'); var publishedDate = e.feed.entry[t].published.$t; linkElement.href = link; linkElement.textContent = title; dateElement.className = 'publish-date timeago fs-08'; dateElement.setAttribute('data-publish', 'data:post.date.iso8601'); dateElement.setAttribute('title', publishedDate); dateElement.textContent = ''; if (!isCurrentPost) { listItem.appendChild(linkElement); listItem.appendChild(dateElement); listContainer.appendChild(listItem); numChapters++; } else { listItem.appendChild(linkElement); listItem.appendChild(dateElement); listItem.classList.add('current-post'); listContainer.appendChild(listItem); } var chapNumElement = document.getElementById('chap-num'); chapNumElement.textContent = numChapters; } } }
