$(document).ready(function(){
  var $prev = $('.previous');
  var $next = $('.next');
  var mode = "auto";
  $prev.on({
    click: function(e){
      e.preventDefault();
      mode = "manual";
      showPreviousImage();
    }
  });
  $next.on({
    click: function(e){
      e.preventDefault();
      mode = "auto";
      showNextImage();

    }
  });

  setInterval(function(){
    if(mode==="auto"){
      showNextImage();
    }
  },2000);

  function showNextImage(){
      var $actEl = $('.active');
      var $nextEl = $actEl.next('.slide');
      if($nextEl.length){
        $actEl.removeClass('active');
        $nextEl.addClass('active');
      }else{
        $actEl.removeClass('active');
        $('.slide:first-child').addClass('active');
      }
  }

  function showPreviousImage(){
      var $actEl = $('.active');
      var $prevEl = $actEl.prev('.slide');
      if($prevEl.length){
        $actEl.removeClass('active');
        $prevEl.addClass('active');
      }else{
        $actEl.removeClass('active');
        $('.slide.last').addClass('active');
      }
  }
});

jQuery(document).ready(function ($) {

	var slideCount = $('#quote-slider ul li').length;
	var slideWidth = $('#quote-slider ul li').width();
	var slideHeight = $('#quote-slider ul li').height();
	var sliderUlWidth = slideCount * slideWidth;

	$('#quote-slider').css({ width: slideWidth, height: slideHeight });

	$('#quote-slider ul').css({ width: sliderUlWidth, marginTemplatezy:  slideWidth });

    $('#quote-slider ul li:last-child').prependTo('#quote-slider ul');

    function moveLeft() {
        $('#quote-slider ul').animate({
            left: + slideWidth
        }, 200, function () {
            $('#quote-slider ul li:last-child').prependTo('#quote-slider ul');
            $('#quote-slider ul').css('left', '');
        });
    };

    function moveRight() {
        $('#quote-slider ul').animate({
            left: - slideWidth
        }, 200, function () {
            $('#quote-slider ul li:first-child').appendTo('#quote-slider ul');
            $('#quote-slider ul').css('left', '');
        });
    };

    $('a.control_prev').click(function () {
        moveLeft();
    });

    $('a.control_next').click(function () {
        moveRight();
    });

});
