function showgallerypostsv(e) {
    var t = e.feed.openSearch$totalResults.$t;
    var n = new Array;
    document.write("<div class='breaking-ticker'>");
    for (var r = 0; r < t; ++r) {
        n[r] = r
    }
    if (random_posts == true) {
        n.sort(function () {
            return .5 - Math.random()
        })
    }
    if (numposts_gal > t) {
        numposts_gal = t
    }
    for (r = 0; r < numposts_gal; ++r) {
        var i = e.feed.entry[n[r]];
        var o = i.title.$t;
        for (var u = 0; u < i.link.length; u++) {
            if (i.link[u].rel == "alternate") {
                posturl_gal = i.link[u].href;
                break
            }
        }
        if ("content" in i) {
            var f = i.content.$t
        }
        s = f;
        a = s.indexOf("<img");
        b = s.indexOf('src="', a);
        c = s.indexOf('"', b + 5);
        d = s.substr(b + 5, c - b - 5);
        if (a != -1 && b != -1 && c != -1 && d != "") {
            var l = d
        } else var l = "http://4.bp.blogspot.com/-ZoL4BRpPhkw/U6wnup4_6gI/AAAAAAAAACA/sKpM61pmJPo/s1600/no_pic_available.jpg";
        document.write('<div class="post-div">');
        document.write('<a href="' + posturl_gal + '">' + o + "</a>");
        document.write("</div>")
    }
    document.write("</div>")
}
