var blog = document.location.hostname.split(".");
if (window.location.href.indexOf(".blogspot") > -1) {
    if (blog[blog.length - 1] != "com") {
        var ncr = "http://" + blog[0] + ".blogspot.com/ncr";
        window.location.replace(ncr + document.location.pathname);
    }
}

