  div.innerHTML = getSummaryLikeWP(pID);
  div.style.display = "block";
 } 
 else {

 var imgtag = "";
 var img = div.getElementsByTagName("img");
 var summ = summary;
 if(img.length>=1) { 
   imgtag = '<a href="'+posturl+'"><img src="'+img[0].src+'"></a>';
   }
  var summary1 = '<div class="article-thumbnail">'+imgtag+'</div> <div class="article-content">  <div class="article-excerpt"><p>'+stripHtmlTags(content,summary)+'...<a href="'+posturl+'" title="read more">continue reading</a></p></div><div class="clear"></div></div>';

 div.innerHTML = summary1;
 div.style.display = "block";
 }
