function resizeThumb(size) {
    var popularPost = document.getElementById('imagesize');
    var image = popularPost.getElementsByTagName('img');
    for (var i = 0; i < image.length; i++) {
        image[i].src = image[i].src.replace(/\/s72\-c/g, '\/s' + size);
    }
}
