body.script:
function moveScroller() {
    var move = function() {
        var st = $(window).scrollTop();
        var ot = $("#menuscroll").offset().top;
        var s = $("#menusearch");
        if(st > ot) {
            s.css({
                position: "fixed",
                top: "0px"
            });
        } else {
            if(st <= ot) {
                s.css({
                    position: "relative",
                    top: ""
                });
            }
        }
    };
    $(window).scroll(move);
    move();
}
  $(function() {
    moveScroller();
  });

